import React, { FC, useCallback, useState } from 'react';
import { Card, Image, Typography } from 'antd';
import type { CardProps } from 'antd';
import { css, ClassNames } from '@emotion/react';
import tw from 'twin.macro';
import type { Contractor } from '@modules/crm/types/Contractor';
import { AvatarListWithDescriptions } from '@modules/crm/components/molecules/AvatarListWithDescriptions';
import { ContactInfoLink } from '../atoms/ContactInfoLink';
import { Highlighted } from '@components/atoms/Highlighted';

type CardContractorProps = CardProps & {
  contractor: Contractor;
  searchTerm: string;
};

export const CardContractor: FC<CardContractorProps> = ({
  contractor,
  searchTerm,
  ...rest
}) => {
  const [hovered, setHovered] = useState(false);
  const handleMouseEnter = useCallback(() => {
    setHovered(true);
  }, []);
  const handleMouseLeave = useCallback(() => {
    setHovered(false);
  }, []);
  return (
    <Card
      css={css`
        ${tw`rounded-none cursor-pointer`}
        border-color: ${hovered ? '#2E51A0' : '#d4d3d4'};
        ${hovered && `box-shadow: 0 0 0 2px #c3d8f9;`}
        .ant-card-body {
          ${tw`pb-0`}
        }
      `}
      {...rest}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <Typography.Title className="text-base">
        <Highlighted text={contractor.companyName} highlight={searchTerm} />
      </Typography.Title>
      <Typography.Text>
        Регион:{' '}
        <Highlighted text={contractor.regionId} highlight={searchTerm} />
      </Typography.Text>
      <div css={tw`w-full py-5`}>
        <ClassNames>
          {({ css, cx }) => (
            <Image
              wrapperClassName={css`
                ${tw`block`}
              `}
              css={tw`w-auto mx-auto`}
              height={116}
              src={contractor?.companyLogoUrl ?? 'error'}
              alt={contractor.companyName}
              preview={false}
              fallback="data:image/png;base64,"
            />
          )}
        </ClassNames>
      </div>
      <div
        css={css`
          ${tw`w-full py-5`}
          min-height: 0;
          max-height: 9999px;
          overflow: hidden;
          transition: max-height 1s;
          ${!hovered && contractor.contacts.length > 0 && tw`max-h-0 py-0`}
        `}
      >
        <div>
          <Typography.Text type="secondary">
            Юридический адрес:{' '}
          </Typography.Text>
          <Typography.Text>
            <Highlighted
              text={contractor?.companyName}
              highlight={searchTerm}
            />
          </Typography.Text>
        </div>
        <div>
          <Typography.Text type="secondary">Тел.: </Typography.Text>
          <Typography.Text>
            <Highlighted
              text={contractor?.phoneNumber}
              highlight={searchTerm}
            />
          </Typography.Text>
        </div>
        <div>
          <Typography.Text type="secondary">Факс: </Typography.Text>
          <Typography.Text>
            <Highlighted text={contractor?.faxNumber} highlight={searchTerm} />
          </Typography.Text>
        </div>
        <div>
          <Typography.Text type="secondary">Эл. адрес: </Typography.Text>
          <ContactInfoLink href={`mailto:${contractor?.email}`}>
            <Highlighted text={contractor?.email} highlight={searchTerm} />
          </ContactInfoLink>
        </div>
      </div>
      <AvatarListWithDescriptions contractor={contractor} />
    </Card>
  );
};
